mapper.update:
update waybill
set status = 1
where tntnumber = #{tntnumber}

mapper.select:
select tntnumber, senname, senaddress, senzipcode, senphonenum, goodsname, recname, recphonenum, recaddress, reczipcode, carnum, arrive, wid, status from waybill
and status = #{status}
and tntnumber like '%${tntnumber}%'
